mapper.select:
select s.*,c.name categoryName from setmeal s left join category c on s.category_id = c.id
and s.name like concat('%',#{name},'%')
and s.category_id = #{categoryId}
and s.status = #{status}
order by create_time desc

mapper.delete:
delete from setmeal
where id in

    #{id}

mapper.update:
update setmeal
category_id = #{categoryId}, name = #{name}, price = #{price}, status = #{status}, description = #{description}, image = #{image}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select id, category_id, name, price, status, description, image, create_time, update_time,
create_user, update_user from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


